import{_ as e,c as d,o,a3 as l}from"./chunks/framework.C8Xs1bna.js";const p=JSON.parse('{"title":"用户管理","description":"","frontmatter":{},"headers":[],"relativePath":"C++/3.C++进阶课程/第1节linux基础/3.用户管理.md","filePath":"C++/3.C++进阶课程/第1节linux基础/3.用户管理.md","lastUpdated":1754900608000}'),a={name:"C++/3.C++进阶课程/第1节linux基础/3.用户管理.md"};function r(i,t,c,u,s,h){return o(),d("div",null,t[0]||(t[0]=[l('<h1 id="用户管理" tabindex="-1">用户管理 <a class="header-anchor" href="#用户管理" aria-label="Permalink to &quot;用户管理&quot;">​</a></h1><h2 id="用户信息存储" tabindex="-1">用户信息存储 <a class="header-anchor" href="#用户信息存储" aria-label="Permalink to &quot;用户信息存储&quot;">​</a></h2><ul><li><p><strong>用户信息文件</strong>：<code>/etc/passwd</code></p><ul><li>格式：<code>用户名:密码(口令):用户ID号:组用户ID号:用户家目录:用户的Shell目录</code></li><li>示例：<code>root:x:0:0:root:/root:/bin/bash</code></li></ul></li><li><p><strong>用户组信息文件</strong>：<code>/etc/group</code></p><ul><li>格式：<code>用户组名:密码:组ID号:成员列表</code></li><li>示例：<code>root:x:0:</code></li></ul></li></ul><h2 id="增加用户" tabindex="-1">增加用户 <a class="header-anchor" href="#增加用户" aria-label="Permalink to &quot;增加用户&quot;">​</a></h2><ul><li><strong>adduser</strong>：新增用户 <ul><li>用法：<code>sudo adduser 用户名称</code></li><li>示例：<code>sudo adduser tmpuser</code><ul><li>执行流程： <ol><li>输入sudo密码</li><li>创建用户&quot;tmpuser&quot;</li><li>创建新组&quot;tmpuser&quot; (ID 1001)</li><li>添加用户到组</li><li>创建主目录<code>/home/tmpuser</code></li><li>从<code>/etc/skel</code>复制配置文件</li><li>设置密码（需满足长度≥8字符）</li><li>填写用户信息（全名/房间号/电话等）</li><li>确认信息<code>[Y/n]</code></li></ol></li></ul></li></ul></li></ul><h2 id="删除用户" tabindex="-1">删除用户 <a class="header-anchor" href="#删除用户" aria-label="Permalink to &quot;删除用户&quot;">​</a></h2><ul><li><strong>userdel</strong>：删除用户 <ul><li>用法：<code>sudo userdel 登录名</code></li><li>示例：<code>sudo userdel tmpuser</code></li></ul></li></ul><blockquote><p><strong>注意</strong>：删除用户不会清除用户目录</p></blockquote><h2 id="用户组操作" tabindex="-1">用户组操作 <a class="header-anchor" href="#用户组操作" aria-label="Permalink to &quot;用户组操作&quot;">​</a></h2><h3 id="用户组作用" tabindex="-1">用户组作用 <a class="header-anchor" href="#用户组作用" aria-label="Permalink to &quot;用户组作用&quot;">​</a></h3><p>隔离权限，实现权限分组管理</p><h3 id="增加用户组" tabindex="-1">增加用户组 <a class="header-anchor" href="#增加用户组" aria-label="Permalink to &quot;增加用户组&quot;">​</a></h3><ul><li><strong>groupadd</strong>：新增用户组 <ul><li>用法：<code>sudo groupadd 组名称</code></li><li>示例：<code>sudo groupadd vrpclass</code></li></ul></li></ul><h3 id="删除用户组" tabindex="-1">删除用户组 <a class="header-anchor" href="#删除用户组" aria-label="Permalink to &quot;删除用户组&quot;">​</a></h3><ul><li><strong>groupdel</strong>：删除用户组 <ul><li>用法：<code>sudo groupdel 组名称</code></li><li>示例：<code>sudo groupdel vrpclass</code></li></ul></li></ul><h3 id="管理组内用户" tabindex="-1">管理组内用户 <a class="header-anchor" href="#管理组内用户" aria-label="Permalink to &quot;管理组内用户&quot;">​</a></h3><ul><li><strong>添加用户到组</strong>：<br><code>adduser 用户名 组名</code></li><li><strong>从组移除用户</strong>：<br><code>deluser 用户名 组名</code></li></ul><h2 id="apt软件包管理工具" tabindex="-1">APT软件包管理工具 <a class="header-anchor" href="#apt软件包管理工具" aria-label="Permalink to &quot;APT软件包管理工具&quot;">​</a></h2><h3 id="工作原理" tabindex="-1">工作原理 <a class="header-anchor" href="#工作原理" aria-label="Permalink to &quot;工作原理&quot;">​</a></h3><ul><li>源服务器配置：<code>/etc/apt/sources.list</code></li><li>修改源地址：<code>sudo gedit /etc/apt/sources.list</code></li><li>更新软件源：<code>sudo apt-get update</code></li></ul><h3 id="常用操作" tabindex="-1">常用操作 <a class="header-anchor" href="#常用操作" aria-label="Permalink to &quot;常用操作&quot;">​</a></h3><table tabindex="0"><thead><tr><th>命令</th><th>功能描述</th></tr></thead><tbody><tr><td><code>sudo apt-get update</code></td><td>更新可用软件包列表</td></tr><tr><td><code>sudo apt-cache search 关键字</code></td><td>搜索软件包</td></tr><tr><td><code>sudo apt-get install 包名</code></td><td>安装软件包</td></tr><tr><td><code>sudo apt-get remove 包名</code></td><td>移除软件包</td></tr><tr><td><code>sudo apt-get reinstall 包名</code></td><td>重新安装软件包</td></tr><tr><td><code>sudo apt-get autoremove</code></td><td>卸载自动安装且不再使用的软件包</td></tr><tr><td><code>sudo apt-get upgrade</code></td><td>通过安装/升级更新系统</td></tr><tr><td><code>sudo apt-get full-upgrade</code></td><td>通过卸载/安装/升级更新系统</td></tr><tr><td><code>sudo apt edit-sources</code></td><td>编辑软件源信息文件</td></tr><tr><td><code>sudo apt satisfy 依赖关系</code></td><td>使系统满足依赖关系字符串</td></tr></tbody></table><h2 id="g-编译工具套件" tabindex="-1">g++编译工具套件 <a class="header-anchor" href="#g-编译工具套件" aria-label="Permalink to &quot;g++编译工具套件&quot;">​</a></h2><h3 id="编译文件" tabindex="-1">编译文件 <a class="header-anchor" href="#编译文件" aria-label="Permalink to &quot;编译文件&quot;">​</a></h3><ul><li><strong>基础编译</strong>：<br><code>g++ 源代码.cpp</code><ul><li>生成默认程序<code>a.out</code></li></ul></li><li><strong>指定输出名</strong>：<br><code>g++ 源代码.cpp -o 程序名</code><ul><li>示例：<code>g++ untitled-1.cpp -o myapp</code></li></ul></li></ul><h3 id="运行程序" tabindex="-1">运行程序 <a class="header-anchor" href="#运行程序" aria-label="Permalink to &quot;运行程序&quot;">​</a></h3><ul><li>语法：<code>./程序名</code></li><li>示例：<code>./a.out</code> 或 <code>./myapp</code></li></ul><blockquote><p><strong>注意</strong>：命令与参数间必须有空格</p></blockquote><h2 id="进程管理" tabindex="-1">进程管理 <a class="header-anchor" href="#进程管理" aria-label="Permalink to &quot;进程管理&quot;">​</a></h2><h3 id="进程定义" tabindex="-1">进程定义 <a class="header-anchor" href="#进程定义" aria-label="Permalink to &quot;进程定义&quot;">​</a></h3><p>正在运行的程序</p><h3 id="显示进程信息" tabindex="-1">显示进程信息 <a class="header-anchor" href="#显示进程信息" aria-label="Permalink to &quot;显示进程信息&quot;">​</a></h3><ul><li><strong>ps</strong>：process status<table tabindex="0"><thead><tr><th>命令</th><th>功能描述</th></tr></thead><tbody><tr><td><code>ps</code></td><td>显示当前终端进程</td></tr><tr><td><code>ps -a</code></td><td>显示当前终端所有进程</td></tr><tr><td><code>ps -e</code></td><td>显示系统所有进程</td></tr><tr><td><code>ps -e | grep 进程名</code></td><td>组合查询特定进程</td></tr></tbody></table></li></ul><h3 id="动态进程监控" tabindex="-1">动态进程监控 <a class="header-anchor" href="#动态进程监控" aria-label="Permalink to &quot;动态进程监控&quot;">​</a></h3><ul><li><strong>top</strong>：动态显示进程信息（按CPU占用率排序） <ul><li>退出：按 <code>q</code></li><li><strong>交互命令</strong>：<table tabindex="0"><thead><tr><th>快捷键</th><th>功能描述</th></tr></thead><tbody><tr><td><code>k</code></td><td>杀死进程（需输入PID）</td></tr><tr><td><code>r</code></td><td>调整进程优先级</td></tr><tr><td><code>d</code> 或 <code>s</code></td><td>设置刷新间隔（秒）</td></tr><tr><td><code>1</code></td><td>切换显示CPU核心详情</td></tr><tr><td><code>m</code></td><td>切换内存显示模式</td></tr><tr><td><code>z</code></td><td>切换彩色/单色显示</td></tr><tr><td><code>u</code></td><td>按用户过滤进程</td></tr></tbody></table></li></ul></li></ul><h3 id="结束进程" tabindex="-1">结束进程 <a class="header-anchor" href="#结束进程" aria-label="Permalink to &quot;结束进程&quot;">​</a></h3><ul><li><strong>kill</strong>：发送信号终止进程 <ul><li>终止指定PID：<code>kill -9 进程ID号</code><ul><li><code>-9</code>：强制终止信号</li></ul></li><li>终止同名进程：<code>killall -9 进程名</code></li></ul></li></ul><h2 id="链接" tabindex="-1">链接 <a class="header-anchor" href="#链接" aria-label="Permalink to &quot;链接&quot;">​</a></h2><h3 id="软链接-符号链接" tabindex="-1">软链接（符号链接） <a class="header-anchor" href="#软链接-符号链接" aria-label="Permalink to &quot;软链接（符号链接）&quot;">​</a></h3><ul><li><strong>特性</strong>： <ul><li>文件A（链接）的inode与文件B（目标）不同</li><li>文件A内容存储文件B的路径</li><li>访问文件A自动重定向到文件B</li><li>删除A不影响B，删除B导致A失效</li></ul></li><li><strong>创建语法</strong>：<br><code>ln -s 源文件 软链接文件名</code></li></ul><h3 id="硬链接" tabindex="-1">硬链接 <a class="header-anchor" href="#硬链接" aria-label="Permalink to &quot;硬链接&quot;">​</a></h3><ul><li><strong>特性</strong>： <ul><li>多个文件名指向同一inode节点</li><li>修改内容影响所有链接文件</li><li>删除任意链接不影响其他访问</li></ul></li><li><strong>创建语法</strong>：<br><code>ln 源文件 硬链接文件名</code></li></ul><blockquote><p><strong>注意</strong>：避免在共享目录（如<code>/mnt/hgfs</code>）使用链接</p></blockquote><h2 id="作业" tabindex="-1">作业 <a class="header-anchor" href="#作业" aria-label="Permalink to &quot;作业&quot;">​</a></h2><p>动手熟悉所有指令操作</p>',45)]))}const b=e(a,[["render",r]]);export{p as __pageData,b as default};
